/*
  	곱하기 연습
 */

public class multi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0; // 앞자리
		int b = 0; // 뒷자리
		
		for(a=1; a<10;a++) 
		{
			for(b=1; b<10;b++) 
			{
				System.out.println(a+" x "+b+" = "+a*b);
				//윈도우에서 ctrl + alt + down arrow 하면 해당 라인 복사

			}
		}		
	}

}
